import React from 'react'
import './ProductItems.css'
import ProductItem from '../productItem/ProductItem';

import Slider from "react-slick";

function ProductItems () {

    const settings = {
        dots: false,
        speed: 500,
        arrows: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        variableWidth: true,
        infinite: true,
        responsive: [
            {
              breakpoint: 2024,
              settings: {
                slidesToShow: 3
              }
            },
            {
              breakpoint: 600,
              settings: {
                slidesToShow: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1
              }
            }
          ]
    }

    return (
        <div className="productItems" >
                <Slider {...settings}>
                    <ProductItem />
                </Slider>
        </div>
    )
}

export default ProductItems
