import React, { useEffect, useState } from 'react'
import { db } from '../../firebase'
import Button from '../button/Button'

import './ProductItems.css'

function ProductItems({collectionName}) {

    const [products, setProducts] = useState([])

    useEffect(() => {
        const fetchProducts = async () => {
            const productsCollection = await db.collection('rolls').get()
            setProducts(productsCollection.docs.map(doc => {
                return doc.data()
            }))
        }

        fetchProducts()
    }, [])

    function truncate(string, n) {
        return string?.length > n ? string.substr(0, n - 1) + '...' : string;
    }

    return (
        <>
            <h1>{collectionName}</h1>
            <div className="productItems">
                {products.map((item) => {
                    return (
                            <div className="productItems__item" key = {item.title}>
                                <div className="productItems__image">
                                    <img src={item.image} alt={item.title}/>
                                    <div className="productItems__fav">
                                        <span>
                                            <i class="fa fa-heart-o" aria-hidden="true"></i>
                                        </span>
                                    </div>
                                    {item?.eco ? <div className="productItems__eco">
                                        <span>
                                            <i class="fa fa-leaf" aria-hidden="true"></i>
                                        </span>
                                    </div> : null}
                                    {item?.spicy ? <div className="productItems__spicy">
                                        <span>
                                            <i class="fa fa-fire" aria-hidden="true"></i>
                                        </span>
                                    </div> : null}
                                
                                </div>
                                <div className="productItems__cart">
                                    <h4>{item.title}</h4>
                                    <div className="productItems__desc">
                                        <h5><span>{item.weight} g </span>{truncate(item.desc, 55)}</h5>
                                    </div>
                                    <div className="productItems__order">
                                        <Button  
                                            title = "Add to cart"
                                            type = "green"
                                            disabled = {false}
                                        />
                                        <span>{item.price}<small> uah</small></span>
                                    </div>
                                </div>
                            </div>
                    )
                })}
            </div>
        </>
    )
}

export default ProductItems
