import {createSlice} from '@reduxjs/toolkit'

const initialFav = localStorage.getItem('fav')
  ? JSON.parse(localStorage.getItem('fav'))
  : []

const initilaUser =  localStorage.getItem('user')
console.log(initilaUser)

export const favSlice = createSlice({
    name: 'fav',
    initialState: {
        fav: initialFav
    },
    reducers: {
        add: (state, {payload}) => {
            state.fav.push(payload)
            if(initilaUser) localStorage.setItem('fav', JSON.stringify(state.fav))
        },
        del: (state, {payload}) => {
            state.fav = state.fav.filter(({title}) => title !== payload)
            if(initilaUser) {
                localStorage.setItem('fav', JSON.stringify(state.fav))
            } else {
                localStorage.remove('fav', state.fav)
            }
        }
    }
})


export const {add, del} = favSlice.actions;


export const showFavorites = state => state.fav.fav;


export default favSlice.reducer;
