import React, { useEffect, useState } from 'react'
import Cart from '../../features/cart/Cart'
import Cats from '../../features/cats/Cats'
import Slide from '../../features/slide/Slide'
import './Home.css'

import spec1 from '../../images/spec1.svg'
import spec2 from '../../images/spec2.svg'
import spec3 from '../../images/spec3.svg'
import spec4 from '../../images/spec4.svg'
import SpecialItem from '../../features/specialItem/SpecialItem'
import { db } from '../../firebase'

function Home() {
    
    const [products, setProducts] = useState([])

    useEffect(() => {
        const fetchProducts = async () => {
            const productsCollection = await db.collection('products').then((docRef) => {
                console.log(docRef.id)
            })
            setProducts(productsCollection.docs.map(doc => {
                return doc.data()
            }))
        }

        fetchProducts()
    }, [])

    console.log(products);

    return (
        <div className="home">
            <Cats />
                <div className="home__content">
                    <Slide />
                    <div className="home__items">
                        <SpecialItem
                            image =  {spec1}
                            title = "Fresh made"
                            desc = "We guarantee freshness of products and do not use frozen fish"
                        />
                        <SpecialItem
                            image =  {spec2}
                            title = "Chef recipes"
                            desc = "We offer both classic sushi and original recipes from the chef"
                        />
                        <SpecialItem
                            image =  {spec3}
                            title = "Quality ingredients"
                            desc = "We do not use flavor enhancers. All products are purchased from trusted suppliers"
                        />
                        <SpecialItem
                            image =  {spec4}
                            title = "Clean kitchen"
                            desc = "The whole cooking process takes place in accordance with all sanitary standards"
                        />

                        <ul>
                            {products.map((item) => {
                                return (
                                    <>
                                        <h4>{item.title}</h4>
                                        <h5>{item.desc}</h5>
                                    </>
                                )
                            })}
                        </ul>
                    </div>
                </div>
            <Cart />
        </div>
    )
}

export default Home
