import {createSlice} from '@reduxjs/toolkit'

export const favSlice = createSlice({
    name: 'fav',
    initialState: {
        fav: [
            {price:"200",
            eco:true,
            image:"https://firebasestorage.googleapis.com/v0/b/shop-4c3fa.appspot.com/o/60.png?alt=media&token=7c3702f1-68de-4b07-854f-ad6542e584a2",
            fav:false,
            desc:"- nori, cucumber, rice, avocado, peanut sauce, tofu, wakame seaweed",
            weight:"315",
            title:"Vega Avocado",
            id:"3",
            spicy:false}
        ]
    },
    reducers: {
        add: (state, {payload}) => {
            state.fav.push(payload)
        },
        del: (state, {payload}) => {
            state.fav = state.fav.filter(({id}) => id !== payload)
        }
    }
})


export const {add, del} = favSlice.actions;


export const showFavorites = state => state.fav.fav;


export default favSlice.reducer;
