import {createSlice} from '@reduxjs/toolkit';

const initialCart = localStorage.getItem('cartItems')
  ? JSON.parse(localStorage.getItem('cartItems'))
  : []
let count = 1;  
  export const cartSlice = createSlice({
    name: 'cartItems',
    initialState: {
        cartItems: initialCart
    },
    reducers: {
        addToCart: (state, {payload}) => {
            let newObj = {...payload, count: count}
            const item = state.cartItems.filter(({title}) => title !== payload.title);
            if(item) {
                count = count + 1
                state.cartItems.push(newObj)
            } else {
                state.cartItems.push(newObj)
            }
            localStorage.setItem('cartItems', JSON.stringify(state.cartItems))
        },
        delFromCart: (state, {payload}) => {
            state.cartItems = state.cartItems.filter(({title}) => title !== payload.title);
            localStorage.setItem('cartItems', JSON.stringify(state.cartItems));
        }
    }
})


export const {addToCart, delFromCart} = cartSlice.actions;


export const showCartItems = state => state.cartItems.cartItems;


export default cartSlice.reducer;
