import React, { useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import auth, { provider } from '../../firebase';
import { useAuthState } from 'react-firebase-hooks/auth';

import logo from '../../images/ninja-logo.svg';
import heart from '../../images/heart.svg';
import people from '../../images/people.svg';
import { useDispatch, useSelector } from 'react-redux';
import "./Header.css";
import {isLoading, login, logout, selectUser } from '../user/userSlice';

function Header() {

    const userActive = useSelector(selectUser);
    let [user] = useAuthState(auth);

    const history = useHistory();
    const dispatch = useDispatch();

    const signIn = () => {
        if(userActive) {
            dispatch(logout())
        } else {
            auth.signInWithRedirect(provider).catch((error) => 
            alert(error.message))
        }
    }

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged((userAuth) => {
          if(userAuth) {
            dispatch(
                login({
                    uid: userAuth.uid,
                    email: userAuth.email
                }))
          } else {
            // Logged out
            dispatch(logout())
          }
        })
        return unsubscribe;
      }, [dispatch])

    

    return (
        <header className="header container-fluid">
            <img onClick={() => history.push("/")} className="header__logo" src={logo} alt="logo"/>
            <div className="header__nav">
                <ul className="header__items">
                    <li onClick={() => history.push("/")}>Home</li>
                    <li onClick={() => history.push("/menu")}>Menu</li>
                    <li onClick={() => history.push("/delivery")}>Delivery</li>
                    <li onClick={() => history.push("/customers")}>For customers</li>
                    <li onClick={() => history.push("/news")}>News</li>
                </ul>
            </div>
            <div className="header__right">
                <div className="header__favorites">
                    <img src={heart} alt="heart"/>
                    <span>Favorites</span>
                </div>
                <div onClick={signIn} className="header__login">
                    <img src={people} alt="people"/>
                    { userActive ?  <span>{user.displayName}</span> : <span>Log in</span>}
                </div>
            </div>
        </header>
    )
}

export default Header
