import React, { useCallback, useState } from 'react'
import MenuItem from '../menuItem/MenuItem'
import './Cart.css'
import cart from '../../images/cart.svg'
import Button from '../button/Button'

import {useDispatch, useSelector} from 'react-redux'
import { showCartItems } from '../../features/cart/cartSlice'
import { delFromCart } from '../../features/cart/cartSlice';

function Cart() {

    const minSum = 400;
    let countSum = 0;
    let isDisabled = true;

    const cartItems = useSelector(showCartItems);
    const dispatch = useDispatch()
    let [products, setProducts] = useState([])

    const deleteItem = useCallback((index) => {
        const addItemIndex = cartItems.findIndex(({title}) => title === products[index].title);
        
        if(addItemIndex >= 0) {
            dispatch(
                delFromCart(products[index])
            )
        }
    }, [dispatch, cartItems, products])

    const items = cartItems.map((item, idx)=> {
            countSum += parseInt(item.price)
            if(countSum > minSum) {
                isDisabled = false;
            }
            return (
                <div key={item.title} className="cart-item">
                    <div className="cart-info">
                        <span className="cart-info__name">{item.title}</span>
                        <span className="cart-info__count">{item.price} uah</span>
                    </div>
                    <img src={item.image} alt={item.title} />
                    <span onClick = {() => deleteItem(idx)}>
                        <i className="fa fa-times delete" aria-hidden="true"></i>
                    </span>
                </div>
            )
    })

    

    return (
        <div className="cart">
            {cartItems ? items : <div className="cart__empty">
                <div className="cart__info">
                    <img src={cart} alt=""/>
                    <span>Add a product to cart. Everything is very delicious here</span>
                </div>

                <div className="cart__items">
                    <MenuItem 
                        title="rolls" 
                    />
                    <MenuItem 
                            title="sushi" 
                        />
                    <MenuItem 
                            title="sets" 
                        />
                    <MenuItem 
                            title="snacks" 
                        />
                    <MenuItem 
                            title="drinks" 
                        />
                    <MenuItem 
                            title="sauces" 
                        />
                </div>

                <Button 
                    title = "Order history"
                    type = "orange"
                    disabled = {false}
                />
            </div>}
            

            <div className="cart__order">
                {countSum < minSum ? <p>Minimum order sum {minSum} uah</p> : null} 
                
                <div className="cart__order-wrapper">
                    <div className="cart__order-count">
                        <small>Total:</small>
                        <span>{countSum} <span>uah</span></span>
                    </div>
                    <Button 
                        title = "Checkout"
                        type = {isDisabled ? "inactive" : "active" }
                        disabled = {isDisabled}
                    />
                </div>
            </div>

        </div>
    )
}

export default Cart
