import React, { useCallback, useEffect, useState } from 'react'
import { db } from '../../firebase'
import Button from '../button/Button'

import './ProductItems.css'

import Slider from "react-slick";
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useCollection } from 'react-firebase-hooks/firestore';
import { add, del, showFavorites } from '../../features/fav/favSlice';
import { addToCart, addProducts } from '../../features/cart/cartSlice';

function ProductItems ({collectionName, activeSlider, button, showFavs, hideTitle}) {
    

    let [products, setProducts] = useState([])
    const favItem = useSelector(showFavorites)
    const [collItems] = useCollection(db.collection(collectionName))  
    const history = useHistory()
    const dispatch = useDispatch()
    let [mounted, setMounted] = useState(true)
    const settings = {
        dots: false,
        speed: 500,
        arrows: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        variableWidth: true,
        infinite: true,
        responsive: [
            {
              breakpoint: 2024,
              settings: {
                slidesToShow: 3
            }
        },
        {
            breakpoint: 600,
            settings: {
                slidesToShow: 2
            }
        },
        {
            breakpoint: 480,
            settings: {
                slidesToShow: 1
            }
        }
    ]
}


const toggleFav = useCallback((index) => {
    const addItemIndex = favItem.findIndex(({title}) => title === products[index].title);
    const itemIndex = collItems.docs[index].id;
    
    if(addItemIndex < 0) {
        db.collection(collectionName).doc(itemIndex).update({
            fav: true
        })
        
        dispatch(
            add(products[index])
            )
            setMounted(!mounted) 
        } else {
            db.collection(collectionName).doc(itemIndex).update({
                fav: false
            })
            
            dispatch(
                del(products[index])
                )
                setMounted(!mounted)  
            } 
            
        }, [dispatch, favItem, products,mounted,collItems, collectionName])
        
        
        const addToCartList = useCallback((index) => {
            dispatch(
                addToCart(products[index])
                ) 
            }, [dispatch, products])
            
            
            useEffect(() => {
                
                const fetchProducts = async () => {
                    const productsCollection = await db.collection(collectionName).get()
                    setProducts(productsCollection.docs.map(doc => {
                        return doc.data()
                    }))
                }
                
                fetchProducts()
            }, [collectionName, mounted])

            useEffect(() => {
                dispatch(
                    addProducts(products)
                )
            }, [dispatch, products])
            
            const truncate = (string, n) => {
                return string?.length > n ? string.substr(0, n - 1) + '...' : string;
            }
            
            
            
            if(showFavs) {
                    products = favItem
                }
                
                const items = products.map((item, idx) => {
                    return (
                        <div 
                        className="productItems__item" 
                        key = {item.title} 
                        style = {
                            activeSlider ? {width: 300} : {}
                        }
                        >
                        <div className="productItems__image">
                            <img src={item.image} alt={item.title}/>
                            <div className="productItems__fav">
                                {showFavs ? null :  <span onClick={() => toggleFav(idx)}>
                                    <i 
                                    className={`fa fa-heart-o ${item.fav ? 'active' : ''}`} 
                                    aria-hidden="true"></i>
                                    
                                </span>}
                               
                            </div>
                            {item?.eco ? <div className="productItems__eco">
                                <span>
                                    <i className="fa fa-leaf" aria-hidden="true"></i>
                                </span>
                            </div> : null}
                            {item?.spicy ? <div className="productItems__spicy">
                                <span>
                                    <i className="fa fa-fire" aria-hidden="true"></i>
                                </span>
                            </div> : null}
                        
                        </div>
                        <div className="productItems__cart">
                            <h4>{item.title}</h4>
                            <div className="productItems__desc">
                                <h5><span>{item.weight} g </span>{truncate(item.desc, 55)}</h5>
                            </div>
                            <div className="productItems__order">
                                <Button
                                    title = "Add to cart"
                                    type = "green"
                                    disabled = {false}
                                    action= {() => addToCartList(idx)}
                                />
                                <span>{item.price}<small> uah</small></span>
                            </div>
                        </div>

                        
                    </div>
            )
    })

    return (
        <div 
            className="productItems" 
            style = {
                !activeSlider ? {display:'flex'} : {}
            }
        >       
                {hideTitle ? null : <h2>{collectionName}</h2>}
                

                {
                    button 
                ? 
                    <Button
                        action = {() => history.push(`/${collectionName}`)}
                        type = "orange"
                        title = {`All ${collectionName}`}
                        disabled = {false}
                    /> 
                : 
                    null
                }
                
                {
                    activeSlider 
                ?  
                    <Slider {...settings}>
                        {items}
                    </Slider>
                : 
                    items
                }

                 
        </div>
    )
}


export default ProductItems
