import React, { useEffect, useState } from 'react'
import { db } from '../../firebase'
import Button from '../button/Button'

import './ProductItems.css'

import Slider from "react-slick";
import { useHistory } from 'react-router-dom';

function ProductItems ({collectionName}, activeSlider) {
    console.log(activeSlider);

    const [products, setProducts] = useState([])
    const history = useHistory()

    const settings = {
        dots: false,
        speed: 500,
        arrows: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        variableWidth: true,
        infinite: true,
        responsive: [
            {
              breakpoint: 2024,
              settings: {
                slidesToShow: 3
              }
            },
            {
              breakpoint: 600,
              settings: {
                slidesToShow: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1
              }
            }
          ]
    }

    const items = products.map((item) => {
            return (
                    <div className="productItems__item" key = {item.title} style={{ width: 300 }}>
                        <div className="productItems__image">
                            <img src={item.image} alt={item.title}/>
                            <div className="productItems__fav">
                                <span>
                                    <i className="fa fa-heart-o" aria-hidden="true"></i>
                                </span>
                            </div>
                            {item?.eco ? <div className="productItems__eco">
                                <span>
                                    <i className="fa fa-leaf" aria-hidden="true"></i>
                                </span>
                            </div> : null}
                            {item?.spicy ? <div className="productItems__spicy">
                                <span>
                                    <i className="fa fa-fire" aria-hidden="true"></i>
                                </span>
                            </div> : null}
                        
                        </div>
                        <div className="productItems__cart">
                            <h4>{item.title}</h4>
                            <div className="productItems__desc">
                                <h5><span>{item.weight} g </span>{truncate(item.desc, 55)}</h5>
                            </div>
                            <div className="productItems__order">
                                <Button
                                    title = "Add to cart"
                                    type = "green"
                                    disabled = {false}
                                />
                                <span>{item.price}<small> uah</small></span>
                            </div>
                        </div>
                    </div>
            )
    })

    useEffect(() => {
        const fetchProducts = async () => {
            const productsCollection = await db.collection(collectionName).get()
            setProducts(productsCollection.docs.map(doc => {
                return doc.data()
            }))
        }

        fetchProducts()
    }, [collectionName])


    function truncate(string, n) {
        return string?.length > n ? string.substr(0, n - 1) + '...' : string;
    }


    return (
        <div className="productItems" >
                <h2>{collectionName}</h2>
                <Button
                    action = {() => history.push(`/${collectionName}`)}
                    type = "orange"
                    title = "See the menu"
                    disabled = {false}
                />

                {!activeSlider ?  <Slider {...settings}>
                    {items}
                </Slider> : {items}}

                 
        </div>
    )
}


export default ProductItems
