import React, { useCallback } from 'react'
import MenuItem from '../menuItem/MenuItem'
import './Cart.css'
import cart from '../../images/cart.svg'
import Button from '../button/Button'

import {useDispatch, useSelector} from 'react-redux'
import { showCartItems } from '../../features/cart/cartSlice'
import { delFromCart, addToCart } from '../../features/cart/cartSlice';

export default function Cart() {

    const minSum = 400;
    let countSum = 0;
    let isDisabled = true;

    const cartItems = useSelector(showCartItems);
    const dispatch = useDispatch()

    const toggleItem = useCallback((index) => {
        const addItemIndex = cartItems.findIndex(({title}) => title !== cartItems[index].title);
        console.log(addItemIndex);
        if(addItemIndex < 0) {
            dispatch(
                delFromCart(cartItems[index])
            )
            
        } else {
            
            dispatch(
                addToCart(cartItems[index])
            ) 
        }

    }, [dispatch, cartItems])

    const items = cartItems.map((item, idx)=> {
            countSum += parseInt(item.price)
            
            if(countSum > minSum) {
                isDisabled = false;
            }
            return (
                <div key={item.title} className="cart-item">
                    <div className="cart-item__info">
                        <span className="cart-item__title">{item.title}</span>
                        <div className="cart-item__price">
                            <span className="cart-item__minus" onClick={() => toggleItem(idx)}></span>
                            <span className="cart-item__count">{item.count}</span>
                            <span className="cart-item__plus" onClick={() => toggleItem(idx)}></span>
                            <span className="cart-info__currency">{item.price} uah</span>
                        </div>
                    </div>
                    <img src={item.image} alt={item.title} />
                    <span onClick = {() => toggleItem(idx)}>
                        <i className="fa fa-times delete" aria-hidden="true"></i>
                    </span>
                </div>
            )
    })

    return (
        <div className="cart">
            {cartItems ? items : <div className="cart__empty">
                <div className="cart__info">
                    <img src={cart} alt=""/>
                    <span>Add a product to cart. Everything is very delicious here</span>
                </div>

                <div className="cart__items">
                    <MenuItem 
                        title="rolls" 
                    />
                    <MenuItem 
                            title="sushi" 
                        />
                    <MenuItem 
                            title="sets" 
                        />
                    <MenuItem 
                            title="snacks" 
                        />
                    <MenuItem 
                            title="drinks" 
                        />
                    <MenuItem 
                            title="sauces" 
                        />
                </div>

                <Button 
                    title = "Order history"
                    type = "orange"
                    disabled = {false}
                />
            </div>}
            

            <div className="cart__order">
                {countSum < minSum ? <p>Minimum order sum {minSum} uah</p> : null} 
                
                <div className="cart__order-wrapper">
                    <div className="cart__order-count">
                        <small>Total:</small>
                        <span>{countSum} <span>uah</span></span>
                    </div>
                    <Button 
                        title = "Checkout"
                        type = {isDisabled ? "inactive" : "active" }
                        disabled = {isDisabled}
                    />
                </div>
            </div>

        </div>
    )
}
