import React, { useEffect, useState } from 'react'
import { db } from '../../firebase'
import Button from '../button/Button'

import './ProductItems.css'

import Slider from "react-slick";
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useCollection } from 'react-firebase-hooks/firestore';
import { add, del, showFavorites } from '../../features/fav/favSlice';

function ProductItems ({collectionName, activeSlider, button, showFavs}) {
    

    let [products, setProducts] = useState([])
    const favItem = useSelector(showFavorites)
    const [collItems, laoding, error] = useCollection(db.collection(collectionName))   
    const history = useHistory()
    const dispatch = useDispatch()
    console.log(collItems);
    const settings = {
        dots: false,
        speed: 500,
        arrows: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        variableWidth: true,
        infinite: true,
        responsive: [
            {
              breakpoint: 2024,
              settings: {
                slidesToShow: 3
              }
            },
            {
              breakpoint: 600,
              settings: {
                slidesToShow: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1
              }
            }
          ]
    }
    
    useEffect(() => {
        const fetchProducts = async () => {
            if(collectionName) {
                const productsCollection = await db.collection(collectionName).get()
                setProducts(productsCollection.docs.map(doc => {
                    return doc.data()
                }))
            } else {
                const productsCollection = await db.collection('shop-4c3fa').get()
                setProducts(productsCollection.docs.map(doc => {
                    return doc.data()
                }))
            }
            
        }
        fetchProducts()
    }, [collectionName])


    const truncate = (string, n) => {
        return string?.length > n ? string.substr(0, n - 1) + '...' : string;
    }

    const toggleFav = (index) => {

        const addItemIndex = favItem.findIndex(({title}) => title === products[index].title);
        
        if(addItemIndex < 0) {
            dispatch(
                add(products[index]),
                db.collection(collectionName).doc("47z5BRqkJCe2IAQkPLXi").update({
                    fav: true
                })
            )
            
        } else {
            dispatch(
                del(products[index].title),
                db.collection(collectionName).doc("47z5BRqkJCe2IAQkPLXi").update({
                    fav: false
                })
            )
        }
    }

    if(showFavs) {
        products = favItem
    }
    
    const items = products.map((item, idx) => {
            return (
                    <div 
                        className="productItems__item" 
                        key = {item.title} 
                        style = {
                            activeSlider ? {width: 300} : {}
                        }
                    >
                        <div className="productItems__image">
                            <img src={item.image} alt={item.title}/>
                            <div className="productItems__fav">
                                <span onClick={() => toggleFav(idx)}>
                                    <i className="fa fa-heart-o" aria-hidden="true"></i>
                                </span>
                            </div>
                            {item?.eco ? <div className="productItems__eco">
                                <span>
                                    <i className="fa fa-leaf" aria-hidden="true"></i>
                                </span>
                            </div> : null}
                            {item?.spicy ? <div className="productItems__spicy">
                                <span>
                                    <i className="fa fa-fire" aria-hidden="true"></i>
                                </span>
                            </div> : null}
                        
                        </div>
                        <div className="productItems__cart">
                            <h4>{item.title}</h4>
                            <div className="productItems__desc">
                                <h5><span>{item.weight} g </span>{truncate(item.desc, 55)}</h5>
                            </div>
                            <div className="productItems__order">
                                <Button
                                    title = "Add to cart"
                                    type = "green"
                                    disabled = {false}
                                />
                                <span>{item.price}<small> uah</small></span>
                            </div>
                        </div>
                    </div>
            )
    })

    return (
        <div 
            className="productItems" 
            style = {
                !activeSlider ? {display:'flex'} : {}
            }
        >
                <h2>{collectionName}</h2>

                {
                    button 
                ? 
                    <Button
                        action = {() => history.push(`/${collectionName}`)}
                        type = "orange"
                        title = "See the menu"
                        disabled = {false}
                    /> 
                : 
                    null
                }
                
                {
                    activeSlider 
                ?  
                    <Slider {...settings}>
                        {items}
                    </Slider>
                : 
                    items
                }

                 
        </div>
    )
}


export default ProductItems
