import {createSlice} from '@reduxjs/toolkit'

const initialFav = localStorage.getItem('fav')
  ? JSON.parse(localStorage.getItem('fav'))
  : []

export const favSlice = createSlice({
    name: 'fav',
    initialState: {
        fav: initialFav
    },
    reducers: {
        add: (state, {payload}) => {
            // payload.fav = true
            state.fav.push(payload)
            localStorage.setItem('fav', JSON.stringify(state.fav))
        },
        del: (state, {payload}) => {
            // payload.fav = false;
            localStorage.setItem('fav', JSON.stringify(state.fav));
            state.fav = state.fav.filter(({title}) => title !== payload.title);
        },
        fav : (state, {payload}) => {
            console.log(payload);
            if(payload.fav) {
                payload.fav = false
            } else {
                payload.fav = true
            }
        }
    }
})


export const {add, del, fav} = favSlice.actions;


export const showFavorites = state => state.fav.fav;


export default favSlice.reducer;
