import React from 'react'
import './Cart.css'
import Button from '../button/Button'

import {useSelector} from 'react-redux'
import { showCartItems } from '../../features/cart/cartSlice'

function Cart() {

    const minSum = 400;
    let countSum = 0;

    const cartItems = useSelector(showCartItems);

    const items = cartItems.map((item, key)=> {
            return (
                <div key={item.title} className="cart-item">
                    <h1>{item.title}</h1>
                    <span>{item.price} uah</span>
                    <img src={item.image} alt={item.title} />
                </div>
            )
    })

    return (
        <div className="cart">
            {items}
            

            <div className="cart__order">
                <p>Minimum order sum {minSum} uah</p>
                <div className="cart__order-wrapper">
                    <div className="cart__order-count">
                        <small>Total:</small>
                        <span>{countSum} <span>uah</span></span>
                    </div>
                    <Button 
                        title = "Checkout"
                        type = "inactive"
                        disabled = {true}
                    />
                </div>
            </div>

        </div>
    )
}

export default Cart
