import {createSlice} from '@reduxjs/toolkit';

const initialCart = localStorage.getItem('cartItems')
  ? JSON.parse(localStorage.getItem('cartItems'))
  : []

  export const cartSlice = createSlice({
    name: 'cartItems',
    initialState: {
        cartItems: initialCart
    },
    reducers: {
        addToCart: (state, {payload}) => {
            let newItem;
            const itemIndex = state.cartItems.findIndex(({title}) => title === payload.title)
            const item = state.cartItems[itemIndex]
            
            // if(item) {
            //     newItem = {
            //         ...payload,
            //         count: item.count + 1,
            //         price: parseInt(item.price) + parseInt(payload.price)
            //     }
            // } else {
            //     newItem = {
            //         ...payload, 
            //         count: 1
            //     }
            // }

            // if(itemIndex >= 0) {
            //     return {
            //         cartItems: [
            //             ...state.cartItems.slice(0, itemIndex),
            //             newItem,
            //             ...state.cartItems.slice(itemIndex + 1)
            //         ]
            //     }
            // }

            if(item) {
                payload.count = item.count + 1
                payload.price = parseInt(item.price) + parseInt(payload.price)
                
            } else {
                payload.count = 1
            }

            if(itemIndex >= 0) {
                payload.count = item.count + 1
                payload.price = parseInt(item.price) + parseInt(payload.price)
            }
           

            state.cartItems.push(newItem)
            
            localStorage.setItem('cartItems', JSON.stringify(state.cartItems))
            
        },
        delFromCart: (state, {payload}) => {
            state.cartItems = state.cartItems.filter(({title}) => title !== payload.title);
            localStorage.setItem('cartItems', JSON.stringify(state.cartItems));
        }
    }
})


export const {addToCart, delFromCart} = cartSlice.actions;


export const showCartItems = state => state.cartItems.cartItems;


export default cartSlice.reducer;
