import React, { useEffect } from 'react'
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom'
import { login } from '../user/userSlice';

import logo from '../../images/logo.png'
import "./Nav.css";
function Header() {

    let history = useHistory();
    const dispatch = useDispatch();

    useEffect(() => {
        const unsubscribe = auth.GoogleAuthProvider((userAuth) => {
            if(userAuth) {
                dispatch(login ({
                    uid: userAuth.uid,
                    email: userAuth.email
                }))
            } else {
                dispatch(logout())
            }
        })

        return unsubscribe;
    }, [dispatch])

    return (
        <div className="container">
            <div className="nav">
                <img onClick={() => history.push("/")} className="nav__logo" src={logo} alt="logo"/>
                <ul className="nav__items">
                    <li onClick={() => history.push("/")}>Home</li>
                    <li onClick={() => history.push("/menu")}>Menu</li>
                    <li onClick={() => history.push("/contact")}>Contact</li>
                </ul>
                <div className="nav__cart">
                    <i onClick={() => history.push("/cart")} className="fa fa-shopping-cart" aria-hidden="true">
                        <div className="nav__cart-count">
                            <span>1</span>
                        </div>
                    </i>
                </div>
                <div onClick={() => dispatch(login())} className="nav__login">
                    <i className="fa fa-sign-in" aria-hidden="true"></i>
                </div>
            </div>
        </div>
    )
}

export default Header
