import React, { useEffect, useState } from 'react'
import { db } from '../../firebase'
import Button from '../button/Button'

import './ProductItems.css'

function ProductItems() {

    const [products, setProducts] = useState([])

    useEffect(() => {
        const fetchProducts = async () => {
            const productsCollection = await db.collection('rolls').get()
            setProducts(productsCollection.docs.map(doc => {
                return doc.data()
            }))
        }

        fetchProducts()
    }, [])

    return (
        <div className="productItems">
            {products.map((item) => {
                return (
                    <div className="productItems__item" key = {item.title}>
                        <div className="productItems__image">
                            <img src={item.image} alt={item.title}/>
                            <div className="productItems__fav">
                                <span></span>
                            </div>
                            <div className="productItems__eco">
                                <span></span>
                            </div>
                            <div className="productItems__spicy">
                                <span></span>
                            </div>
                        </div>
                        <div className="productItems__cart">
                            <h4>{item.title}</h4>
                            <h5><span>{item.weight} g </span>{item.desc}</h5>
                            <div className="productItems__order">
                                <Button  
                                    title = "Add to cart"
                                    type = "green"
                                    disabled = {false}
                                />
                                <span>{item.price} uah</span>
                            </div>
                        </div>
                    </div>
                )
            })}
        </div>
    )
}

export default ProductItems
