import React from 'react'
import { useHistory } from 'react-router-dom'
import auth, { provider } from '../../firebase';
import { useAuthState } from 'react-firebase-hooks/auth';

import logo from '../../images/logo.png'
import { useDispatch } from 'react-redux';
import "./Nav.css";
import { login, logout } from '../user/userSlice';
function Header() {

    const history = useHistory();

    const dispatch = useDispatch();
    let [user] = useAuthState(auth)

    const signIn = () => {
        if(user) {
            dispatch(logout())
        } else {
            auth.signInWithPopup(provider).catch((error) => 
            alert(error.message))
        }
        
    }

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged((userAuth) => {
          if(userAuth) {
            dispatch(login({
              uid: userAuth.uid,
              email: userAuth.email
            }))
          } else {
            // Logged out
            dispatch(logout())
          }
        })
        return unsubscribe;
      }, [])

    return (
        <div className="container">
            <div className="nav">
                <img onClick={() => history.push("/")} className="nav__logo" src={logo} alt="logo"/>
                <ul className="nav__items">
                    <li onClick={() => history.push("/")}>Home</li>
                    <li onClick={() => history.push("/menu")}>Menu</li>
                    <li onClick={() => history.push("/contact")}>Contact</li>
                </ul>
                <div className="nav__cart">
                    <i onClick={() => history.push("/cart")} className="fa fa-shopping-cart" aria-hidden="true">
                        <div className="nav__cart-count">
                            <span>1</span>
                        </div>
                    </i>
                </div>
                <div onClick={signIn} className="nav__login">
                    { user ? <img src={user.photoURL} alt={user.displayName}/> : <i className="fa fa-sign-in" aria-hidden="true"></i>}
                </div>
            </div>
        </div>
    )
}

export default Header
