import {createSlice} from '@reduxjs/toolkit'

export const favSlice = createSlice({
    name: 'favorites',
    initialState: {
        fav: []
    },
    reducers: {
        add: (state, {payload}) => {
            const index = state.fav.findIndex((item) => payload === item.payload);
            state.fav.push(payload[index])
        }
    }
})


export const {add} = favSlice.actions;


export const showFavorites = state => state.favorites.fav;


export default favSlice.reducer;
