import React from 'react'

import Button from '../button/Button'

import './ProductItems.css'

import Slider from "react-slick";
import { useHistory } from 'react-router-dom';
import ProductItem from '../productItem/ProductItem';

function ProductItems ({collectionName}) {

    
    const history = useHistory()

    const settings = {
        dots: false,
        speed: 500,
        arrows: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        variableWidth: true,
        infinite: true,
        responsive: [
            {
              breakpoint: 2024,
              settings: {
                slidesToShow: 3
              }
            },
            {
              breakpoint: 600,
              settings: {
                slidesToShow: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1
              }
            }
          ]
    }

   


    return (
        <div className="productItems" >
                <h2>{collectionName}</h2>
                <Button
                    action = {() => history.push(`/${collectionName}`)}
                    type = "orange"
                    title = "See the menu"
                    disabled = {false}
                />
                <Slider {...settings}>
                    <ProductItem />
                </Slider>
            
        </div>
    )
}

export default ProductItems
