import React, { useEffect, useRef, useState } from 'react'
import { db } from '../../firebase'
import Button from '../button/Button'

import './ProductItems.css'

function ProductItems ({collectionName}) {

    const [products, setProducts] = useState([])
    const [width, setWidth] = useState(0)

    const ref = useRef(null);

    let x = 0;


    useEffect(() => {
        const fetchProducts = async () => {
            const productsCollection = await db.collection(collectionName).get()
            setProducts(productsCollection.docs.map(doc => {
                return doc.data()
            }))
        }

        fetchProducts()
    }, [collectionName])

    useEffect(() => {
       const updateWidth = () => {
           setWidth(ref.current.offsetWidth + 24)
        }

        return updateWidth;
      }, [])

    function truncate(string, n) {
        return string?.length > n ? string.substr(0, n - 1) + '...' : string;
    }

    const showNextItems = () => {
        x += width
        console.log('width', width)
        console.log('x', x)
    }

    return (
        <>
            <div className="productItems__wrapper">
                <h2>{collectionName}</h2>
                <div className="productItems__nav">
                    <i className="fa fa-chevron-left" aria-hidden="true"></i>
                    <span>1/7 {width}</span>
                    <i onClick = {showNextItems} className="fa fa-chevron-right" aria-hidden="true"></i>
                    <Button 
                        type = "orange"
                        title = "See the menu"
                        disabled = {false}
                    />
                </div>
            </div>
            <div 
                className="productItems" 
                style={{
                    transform: `translate3d(${x}px, 0px, 0px)`
                }}
            >
                {products.map((item) => {
                    return (
                            <div ref = {ref} className="productItems__item" key = {item.title}>
                                <div className="productItems__image">
                                    <img src={item.image} alt={item.title}/>
                                    <div className="productItems__fav">
                                        <span>
                                            <i className="fa fa-heart-o" aria-hidden="true"></i>
                                        </span>
                                    </div>
                                    {item?.eco ? <div className="productItems__eco">
                                        <span>
                                            <i className="fa fa-leaf" aria-hidden="true"></i>
                                        </span>
                                    </div> : null}
                                    {item?.spicy ? <div className="productItems__spicy">
                                        <span>
                                            <i className="fa fa-fire" aria-hidden="true"></i>
                                        </span>
                                    </div> : null}
                                
                                </div>
                                <div className="productItems__cart">
                                    <h4>{item.title}</h4>
                                    <div className="productItems__desc">
                                        <h5><span>{item.weight} g </span>{truncate(item.desc, 55)}</h5>
                                    </div>
                                    <div className="productItems__order">
                                        <Button  
                                            title = "Add to cart"
                                            type = "green"
                                            disabled = {false}
                                        />
                                        <span>{item.price}<small> uah</small></span>
                                    </div>
                                </div>
                            </div>
                    )
                })}
            </div>
        </>
    )
}

export default ProductItems
